import React, { useEffect, useState } from 'react';
import data from '../data'
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { detailsProduct, saveProductReview } from '../actions/productActions';
import Rating from '../components/Rating';
import { PRODUCT_REVIEW_SAVE_RESET } from '../constants/productConstants';

function ProductScreen(props) {
  // muestra en consola el id de la promesa
  //console.log(props.match.params.id)
  //el id de los datos tiene que ser igual al id de la promesa
  //const product = data.products.find(x => x._id === props.match.params.id)

  const [qty, setQty] = useState(1);
  const productDetails = useSelector(state => state.productDetails);
  const { product, loading, error } = productDetails;
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(detailsProduct(props.match.params.id));
    return () => {
      //
    };
  }, []);

  const handleAddToCart = () => {
    props.history.push("/cart/" + props.match.params.id + '?qty=' + qty);
  };
  
  return (
    <div>
      <div className='back-to-result'>
        <Link to="/">Back to result</Link>
      </div>
      {loading ? <div>Loading...</div> :
        error ? <div>{error}</div> :
          (
            <div className='details'>
              <div className='details-image'>
                <img src={product.image} alt='..' />
              </div>
              <div className='details-info'>
                <ul>
                  <li>
                    <h4>{product.name}</h4>
                  </li>
                  <li>
                    {product.rating} Stars ({product.numReviews} Reviwes)
                  </li>
                  <li>
                    <b>{product.price}</b>
                  </li>
                  <li>
                    Description:
                    <div>
                      {product.description}
                    </div>
                  </li>
                </ul>
              </div>
              <div className='details-action'>
                <lu>
                  <li>
                    Price: {product.price}</li>
                  <li>
                    Status:{product.countInStock>=0? "In Stock": ""}
                  </li>
                  <li>
                    Qty:<select
                    value={qty}
                    onChange={(e) => {
                      setQty(e.target.value);
                    }}
                  >
                    {
                      [...Array(product.countInStock).keys()].map(x => <option key={x+1} value={x+1}>{x+1}</option>)
                    }
                    </select>
                  </li>
                  <li>
                    {product.countInStock>=0 &&
                    <button onClick={handleAddToCart} className='button'>
                      Add to Cart
                    </button>
                    }

                  </li>
                </lu>
              </div>
            </div>

          )
      }
    </div>
  )
}
export default ProductScreen;
